import React from 'react'
import WeatherForm from '../components/WeatherForm'
import { connect } from 'react-redux'
import { SET_CITY, ADD_CITY } from '../store/actions'
import { fetchCity } from '../../../utils/fetchApi'

const mapStateToProps = state => {
  return {
    targetCity: state.targetCity,
    cities: state.cities,
  }
}

const mapDispatchToProps = dispatch => {
  const setCity = event =>
    dispatch({ type: SET_CITY, payload: event.target.value })

  const addCity = city => {
    fetchCity(city)
      .then(jsonData => {
        console.log(jsonData)
        return jsonData.json()
      })
      .then(data => {
        console.log(data)
      })
      .dispatch({ type: ADD_CITY })
  }

  return {
    setCity: setCity,
    addCity: addCity,
  }
}

const WeatherFormContainer = ({ setCity }) => {
  return <WeatherForm setCity={setCity} />
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(WeatherFormContainer)
