import React from 'react'
import { Field, reduxForm } from 'redux-form'
// import Input from '../../../components/input/Input/Input'
// import Checkbox from '../../../components/input/Checkbox/Checkbox'
import { Button } from 'reactstrap'
import styles from '../styles.scss'

const validate = values => {
  const errors = {}

  if (!values.FirstName) errors.FirstName = 'Povinne pole'
  if (!values.Email) errors.Email = 'Povinne pole'

  return errors
}

const renderField = ({ input, meta, label, type }) => (
  <div>
    <pre>{JSON.stringify(meta, null, 2)}</pre>
    <label>{label}</label>
    <input {...input} placeholder={label} type={type} />
    {meta.error && meta.touched && <span>{meta.error}</span>}
  </div>
)

const Form = ({ handleSubmit, submitting }) => {
  return (
    <form onSubmit={handleSubmit}>
      <Field
        type="text"
        component={renderField}
        label="Krestni jmeno"
        name="FirstName"
      />
      <Field type="text" component={renderField} label="Email" name="Email" />
      <Field type="checkbox" component={renderField} label="GDPR" name="GDPR" />
      <br />
      <Button disabled={submitting} color="danger">
        Odeslat
      </Button>
    </form>
  )
}

export default reduxForm({
  form: 'testForm',
  validate: validate,
})(Form)
