import { createStore, applyMiddleware } from 'redux'
import createReducer from './combineReducers'
import { saveWeatherStorage, saveTodoStorage } from './localStore'


// verze 1.0
// const logger = storeAction => {
//   return next => {
//     return action => {
//       console.log('action', action)
//       const check =  next(action)
//       console.log('lapeno middlewarem', check.type)
//       return check
//     }
//   }
// }


//verze 2.0
const logger = storeAction => next => action => {
  if(action.type === "@@redux-form/BLUR")
  { console.log('lapeno middlewarem', action)}
  
  return action
}
  
const store = createStore(
  createReducer(),
  applyMiddleware(logger),
)
    
// const store = createStore(
//   createReducer(),
//   window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__(),
// )
    
store.subscribe(() => {
  //console.log('store',store.getState())
  saveWeatherStorage(store.getState().weather.cities)
  saveTodoStorage(store.getState().todos.todoTasks)
})

export default store
