import React from 'react'
import WeatherForm from '../components/WeatherForm'
import { connect } from 'react-redux'
import { SET_CITY, ADD_CITY, PIN_CITY } from '../store/actions'
import { saveStorage } from '../../../utils/localStore'

const mapStateToProps = state => {
  return {
    targetCity: state.weather.targetCity,
    cities: state.weather.cities,
  }
}

const fetchWeather = cityName => {
  return fetch(
    `https://api.weatherbit.io/v2.0/forecast/hourly?city=${cityName},cz&key=165c8bbb35604923b89f37cf29003df3&hours=2`,
  )
    .then(response => {
      if (response.status !== 200) {
        alert('Request status = ' + response.status)
      }
      return response.json()
    })
    .then(data => {
      //console.log('DATA: ', data)
      console.log(data.data.map(item => item.temp))

      return data
    })
}

const mapDispatchToProps = dispatch => {
  const setCity = event =>
    dispatch({ type: SET_CITY, payload: event.target.value })
  const addCity = cityName => {
    return fetchWeather(cityName).then(data =>
      dispatch({ type: ADD_CITY, payload: data }),
    )
  }
  const resetCity = () => dispatch({ type: SET_CITY, payload: '' })
  const addCityAndReset = payload => {
    addCity(payload).then(resetCity)
  }

  return {
    setCity: setCity,
    addCityAndReset: addCityAndReset,
  }
}

const WeatherFormContainer = ({
  targetCity,
  cities,
  setCity,
  addCityAndReset,
}) => {
  return (
    <WeatherForm
      setCity={setCity}
      city={targetCity}
      cities={cities}
      addCityAndReset={addCityAndReset}
    />
  )
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(WeatherFormContainer)
