import { connect } from 'react-redux'
import WeatherItem from '../components/WeatherItem'

const mapStateToProps = state => {
  return {
    citiies: state.weather.cities,
  }
}

const WeatherItemContainer = ({ name, data }) => {
  console.log('item data', data)

  const forecast = data.map(hour => {
    const hora = hour.timestamp_local.match(/T([0-7:]{5})/)

    hora = null ? [] : hora

    return (
      <li className="weather-list-item" key={hour.wind_spd}>
        <span className="time">{hora[1]}</span>
        <span className="temp">{hour.temp} Â°C</span>
      </li>
    )
  })

  return <WeatherItem name={name} hours={data.length} temp={forecast} />
}

export default connect(mapStateToProps)(WeatherItemContainer)
