import { ADD_TASK, SET_TASK, REMOVE_TASK, TOGGLE_TASK } from './actions'

const initialState = {
  task: '',
  todoTasks: [],
}

const todoReducer = (state = initialState, action) => {
  switch (action.type) {
  case ADD_TASK:
    return {
      ...state,
      todoTasks: [
        {
          taskId: state.todoTasks.length + 1,
          title: action.payload,
          completed: false,
        },
        ...state.todoTasks,
      ],
    }
  case SET_TASK:
    return {
      ...state,
      task: action.payload,
    }
  case RESET_TASK:
    return{
      ...state,
      task: '',
    }
  case REMOVE_TASK:
    return {
      ...state,
    }
  case TOGGLE_TASK:
    return {
      ...state,
    }
  default:
    return state
  }
}

export default todoReducer
