import { ADD_CITY, REMOVE_CITY, SET_CITY } from './actions'
import {loadStorage} from '../../../utils/localStore'

const preloadedState = loadStorage('weather')

const initialState = {
  targetCity: '',
  cities: preloadedState || [],
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
  case ADD_CITY:
    return {
      ...state,
      cities: [
        ...state.cities,
        {
          name: action.payload.city_name,
          date: new Date(),
          data: action.payload.data,
        },
      ],
    }
  case SET_CITY:
    return {
      ...state,
      targetCity: action.payload,
    }
  case REMOVE_CITY:
    return {
      ...state,
    }
  default:
    return state
  }
}

export default reducer
