import React from 'react'
import WeatherForm from '../components/WeatherForm'
import { connect } from 'react-redux'
import { SET_CITY, ADD_CITY, RESET_CITY } from '../store/actions'

const mapStateToProps = state => {
  return {
    targetCity: state.weather.targetCity,
    cities: state.weather.cities,
  }
}

const fetchWeather = cityName => {
  fetch(
    `https://api.weatherbit.io/v2.0/forecast/hourly?city=${cityName},cz&key=165c8bbb35604923b89f37cf29003df3&hours=2`,
  ).then(response => {
    if (response.status !== 200) {
      alert('Request status = ' + response.status)
    }
    return response.json()
  })
  // .then(data => {
  //   console.log('DATA: ', data)
  //   console.log(data.data.map(item => item.temp))
  //   return data
  // })
}

const mapDispatchToProps = dispatch => {
  const setCity = event =>
    dispatch({ type: SET_CITY, payload: event.target.value })
  const addCity = cityName => {
    const data = fetchWeather(cityName)
    dispatch({ type: ADD_CITY, payload: data })
  }
  const resetCity = () => dispatch({ type: RESET_CITY, payload: '' })

  return {
    setCity: setCity,
    addCity: addCity,
    resetCity: resetCity,
  }
}

const WeatherFormContainer = ({ targetCity, setCity, addCity }) => {
  return <WeatherForm setCity={setCity} city={targetCity} addCity={addCity} />
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(WeatherFormContainer)
