const initialState = {
  coordX: '',
  coordY: '',
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
  case 'getX':
    return {
      ...state,
      coordX: action.payload,
    }
  case 'getY':
    return {
      ...state,
      coordY: action.payload,
    }
  default:
    return state
  }
}

export default reducer
