import React from 'react'
import TodoForm from '../components/TodoForm'
import { ADD_TASK, SET_TASK } from '../store/actions'

const mapStateToProps = state => {
  return {
    todoTasks: state.todoTasks,
    task: state.task,
  }
}

const mapDispatchToProps = dispatch => {
  const addTask = payload => dispatch({ type: ADD_TASK, payload: payload })
  const setTask = event => dispatch({ type: SET_TASK, payload: event })

  return {
    addTask: addTask,
    setTask: setTask,
  }
}

const TodoFormContainer = () => {
  return <TodoForm />
}

export default connect(mapStateToProps, mapDispatchToProps)(TodoFormContainer)
