import React from 'react'
import { connect } from 'react-redux'
import WeatherItem from '../components/WeatherItem'

const mapStateToProps = state => {
  return {
    cities: state.weather.cities,
  }
}

const WeatherItemContainer = ({ name, data }) => {
  
  const forecast = data && data.map(hour => {
    const oneHour = hour.timestamp_local.match(/T([0-9:]{5})/)

    return (
      <li className="weather-list-item" key={hour.wind_spd}>
        <span className="time">{oneHour[1]}</span>
        <span className="temp">{hour.temp} Â°C</span>
      </li>
    )
  })

  return <WeatherItem name={name} hours={data && data.length} temp={forecast} />
}

export default connect(mapStateToProps)(WeatherItemContainer)
