import { ADD_TASK, REMOVE_TASK, TOGGLE_TASK } from './actions'

const initialState = {
    task: ''
}

const todoReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TASK:
            return {
                ...state
            }
        case REMOVE_TASK:
            return {
                ...state
            }
        case TOGGLE_TASK:
            return {
                ...state
            }
        default:
            return state
    }
}

export default todoReducer
