import { ADD_CITY, REMOVE_CITY, RESET_CITY, SET_CITY } from './actions'

const initialState = {
  targetCity: '',
  cities: [],
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
  case ADD_CITY:
    return {
      ...state,
      cities: [
        ...state.cities,
        {
          name: state.targetCity,
          time: new Date().getTime(),
          data: action.payload,
        },
      ],
    }
  case SET_CITY:
    return {
      ...state,
      targetCity: action.payload,
    }
  case REMOVE_CITY:
    return {
      ...state,
    }
  default:
    return state
  }
}

export default reducer
