import React from 'react'
import TodoForm from '../components/TodoForm'
import { ADD_TASK, SET_TASK } from '../store/actions'
import { connect } from 'react-redux'

const mapStateToProps = state => {
  return {
    todoTasks: state.todoTasks,
    task: state.task,
  }
}

const mapDispatchToProps = dispatch => {
  const addTask = payload => {
    console.log(payload)
    dispatch({ type: ADD_TASK, payload: payload })
  }
  const setTask = event =>
    dispatch({ type: SET_TASK, payload: event.target.value })

  return {
    addTask: addTask,
    setTask: setTask,
  }
}

const TodoFormContainer = ({ setTask, addTask }) => {
  return <TodoForm setTask={setTask} addTask={addTask} />
}

export default connect(mapStateToProps, mapDispatchToProps)(TodoFormContainer)
