import { createStore } from 'redux'
import createReducer from './combineReducers'
import { saveStorage } from './localStore'
import { saveTodoStorage } from '../features/Todos/store/todoStorage'

const store = createStore(
  createReducer(),
  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__(),
)


store.subscribe(() => {
  saveStorage(store.getState().weather.cities)
  saveTodoStorage(store.getState().todos.todoTasks)
})

export default store
