const initialState = {
  number: 1,
  coordX: 0,
  coordY: 0,
}

const navbar = (state = initialState, action) => {
  switch (action.type) {
  case 'setCoords':
    return {
      ...state,
      coordX: action.payload,
      coordY: action.payload,
    }
  default:
    return state
  }
}

export default navbar
