import { ADD_CITY, REMOVE_CITY, RESET_CITY, SET_CITY } from './actions'
import {loadStorage} from '../../../utils/localStore'

const initialState = {
  targetCity: '',
  cities: loadStorage('state') || [],
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
  case ADD_CITY:
    console.log('AKCE', action, "PAYLOAD", action.payload)
    return {
      ...state,
      cities: [
        {
          name: action.payload.data.city_name,
          time: new Date(),
          data: action.payload,
        },
        ...state.cities,
      ],
    }
  case SET_CITY:
    return {
      ...state,
      targetCity: action.payload,
    }
  case REMOVE_CITY:
    return {
      ...state,
    }
  default:
    return state
  }
}

export default reducer
