import React from 'react'
import WeatherForm from '../components/WeatherForm'
import { connect } from 'react-redux'
import { SET_CITY, ADD_CITY, UPDATE_CITIES } from '../store/actions'

const mapStateToProps = state => {
  return {
    targetCity: state.weather.targetCity,
    cities: state.weather.cities,
  }
}
// save to localStorage
const clearStorage = itemKey => {
  localStorage.removeItem(itemKey)
  window.location.reload()
}
// update data
const updateForecast = cities => {
  if (cities === []) return

  const updatedCities = cities.map(
    city => (city.data = fetchWeather(city.name)),
  )

  //console.log(updatedCities)
  return updatedCities
}
// fetch API
const fetchWeather = cityName => {
  return fetch(
    `https://api.weatherbit.io/v2.0/forecast/hourly?city=${cityName},cz&key=165c8bbb35604923b89f37cf29003df3&hours=5`,
  )
    .then(response => {
      if (response.status !== 200) {
        alert('Request status = ' + response.status)
      }
      return response.json()
    })
    .then(data => {
      //console.log('DATA: ', data)
      //console.log(data.data.map(item => item.temp))
      return data
    })
}

const mapDispatchToProps = dispatch => {
  const setCity = event =>
    dispatch({ type: SET_CITY, payload: event.target.value })
  const addCity = cityName => {
    return fetchWeather(cityName).then(data =>
      dispatch({ type: ADD_CITY, payload: data }),
    )
  }
  const resetCity = () => dispatch({ type: SET_CITY, payload: '' })
  const addCityAndReset = payload => {
    addCity(payload).then(resetCity)
  }
  const updateCityForecat = cities => {
    return updateForecast(cities).then(data =>
      dispatch({ type: UPDATE_CITIES, payload: data }),
    )
  }

  return {
    setCity: setCity,
    addCityAndReset: addCityAndReset,
  }
}

const WeatherFormContainer = ({
  targetCity,
  cities,
  setCity,
  addCityAndReset,
}) => {
  return (
    <WeatherForm
      updateForecast={updateForecast}
      clearStorage={clearStorage}
      setCity={setCity}
      city={targetCity}
      cities={cities}
      addCityAndReset={addCityAndReset}
    />
  )
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(WeatherFormContainer)
