import React from 'react'
import { Field, reduxForm } from 'redux-form'
import Input from '../../../components/input/Input/Input'
import Checkbox from '../../../components/input/Checkbox/Checkbox'
import { Button } from 'reactstrap'

// Peter, toto je jedno z mnoha řešení, kde se ale berou ty data na BS? koukal jsem se na redux-form tam a tam se do componenty zapisuje to HOC

const renderName = () => (
  <Input labelName="Jmeno" id="firstName" placeholder="Krestni jmeno" />
)

const renderCheckbox = () => <Checkbox labelName="GDPR souhlas" />

const Form = ({ handleSubmit, props }) => {
  console.log('props zde', props)
  //</form> <form onSubmit={handleSubmi(props.showData)}></form> jak by se to muselo zapsat aby to šlo, když bych chtěl showData tahat skrze props?
  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="someText" placeholder="HTML input" />
      <br />
      <br />
      <Field
        type="text"
        component={renderName}
        placeholder="ReduxForm Field"
        name="Field"
      />
      <Field component={renderCheckbox} />
      <Button color="danger">Odeslat</Button>
    </form>
  )
}

export default reduxForm({
  form: 'testForm',
})(Form)
