import { fetchWeather, weatherBit } from '../features/FetchAPI/fetchWeather'
import {
  FETCH_START,
  FETCH_SUCCESS,
  FETCH_FAIL,
} from '../features/Weather/store/actions'

export const fetchForecastStart = (store) => (next) => async (action) => {
  if (action.type === FETCH_START) {
    try {
      const response = await weatherBit.get(action.payload)
    } catch (error) {
      next({ type: FETCH_FAIL, payload: error })
    }
  }

  return next(action)
}

export const fetchForecastSuccess = (store) => (next) => async (action) => {
  return next(action)
}

export const fetchForecastFail = (store) => (next) => async (action) => {
  return next(action)
}
