export const fetchWeather = (cityName) => {
  return fetch(
    `https://api.weatherbit.io/v2.0/forecast/hourly?city=${cityName},cz&key=165c8bbb35604923b89f37cf29003df3&hours=5`,
  )
    .then((response) => {
      if (response.status !== 200) {
        alert('Request status = ' + response.status)
      }
      return response.json()
    })
    .then((data) => {
      //console.log('DATA: ', data)
      //console.log(data.data.map(item => item.temp))
      return data
    })
}
