import React from 'react'
import TodoForm from '../components/TodoForm'

import { connect } from 'react-redux'
import { ADD_TASK, SET_TASK } from '../store/actions'

const mapStateToProps = state => {
  return {
    task: state.task,
    todoTasks: state.todoTasks,
  }
}

const mapDispatchToProps = dispatch => {
  const addTask = payload => {
    console.log(payload)
    dispatch({ type: ADD_TASK, payload: payload })
  }
  const setTask = event =>
    dispatch({ type: SET_TASK, payload: event.target.value })

  return {
    setTask: setTask,
    addTask: addTask,
  }
}

const TodoFormContainer = ({ task, setTask, addTask }) => {
  return <TodoForm task={task} setTask={setTask} addTask={addTask} />
}

export default connect(mapStateToProps, mapDispatchToProps)(TodoFormContainer)
