import { ADD_TASK, REMOVE_TASK, TOGGLE_TASK, SET_TASK } from './actions'

const initialState = {
  task: '',
  todoTasks: [],
}

const todoReducer = (state = initialState, action) => {
  switch (action.type) {
  case SET_TASK:
    return {
      task: action.payload,
      ...state,
    }
  case ADD_TASK:
    return {
      ...state,
      todoTasks: [
        {
          taskId: 1,
          title: action.payload,
          completed: false,
        },
        ...state.items,
      ],
    }
  case REMOVE_TASK:
    return {
      ...state,
    }
  case TOGGLE_TASK:
    return {
      ...state,
    }
  default:
    return state
  }
}

export default todoReducer
