/* WEATHERBIT.IO API */
const SERVER  = 'https://api.weatherbit.io/v2.0/forecast/hourly?'
const API_KEY = '165c8bbb35604923b89f37cf29003df3'

export const fetchCity = (city) => {
  fetch(SERVER + 'city=' + city + '&key=' + API_KEY + '&hours=12')
}

